import rclpy
from rclpy.node import Node
from sensor_msgs.msg import Imu

class IMUSubscriber(Node):

    def __init__(self):
        super().__init__('imu_subscriber')

        # Create a subscriber to listen for IMU data
        self.subscription = self.create_subscription(
                Imu,
                'imu/imu_data', 
                self.callback, 
                10)
        self.subscription


    def callback(self, msg):
        # Print the x, y, and z values of the IMU sensor in one line.
        self.get_logger().info('Linear acceleration: %s' % msg.linear_acceleration)
        self.get_logger().info('Angular velocity: %s' % msg.angular_velocity)


def main(args=None):
    rclpy.init(args=args)

    node = IMUSubscriber()

    # Spin the node indefinitely
    rclpy.spin(node)

    # Shutdown the ROS2 node
    rclpy.shutdown()

if __name__ == '__main__':
    main()

